mapper.update:
update art_market_db.alerts
set alert_status = true
where member_id = #{memberId}

update art_market_db.alerts
set alert_status = true
where member_id = #{memberId}
and alert_type = #{alertType}
and alert_path = #{alertPath}

update art_market_db.alerts set alert_date = #{alertDate}
where alert_id = #{alertId}
and alert_status = false

mapper.select:
select *
from art_market_db.alerts where member_id = #{memberId} and alert_status = false
order by alert_date desc;
