mapper.update:
UPDATE Member
SET identity = #{identity}
WHERE member_id = #{memberId}

UPDATE Member
SET password = #{password}
WHERE member_id = #{memberId}

UPDATE member
SET phone = #{newPhone}
WHERE member_id = #{memberId}

UPDATE member
SET email = #{newEmail}
WHERE member_id = #{memberId}

mapper.select:
SELECT COUNT(*)
FROM Member
WHERE login_id = #{loginId}

SELECT COUNT(*)
FROM Member
WHERE nickname = #{nickname}

SELECT COUNT(*)
FROM Member
WHERE email = #{email}

SELECT COUNT(*)
FROM Member
WHERE phone = #{phone}

mapper.update:
update member
set session_id=#{sessionId},
limit_date=#{limitDate}
where login_id = #{loginId};
